mapper.select:
select * from Role
where 1=1

  and ID = #{id,jdbcType=INTEGER}


   and ROLE_NAME=#{roleName,jdbcType=VARCHAR}

limit  ${start},${pageSize}

select count(*) from Role
where 1=1

    and ID = #{id,jdbcType=INTEGER}


    and ROLE_NAME=#{roleName,jdbcType=VARCHAR}

mapper.update:
update role
set ROLE_NAME = #{roleName,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}
