mapper.select:
select * from `USER`
where 1=1

  and ID = #{id,jdbcType=VARCHAR}


  and USER_NAME = #{userName,jdbcType=VARCHAR}


  and PASS_WORD = #{passWord,jdbcType=VARCHAR}


  and NAME = #{name,jdbcType=VARCHAR}


  and AGE = #{age,jdbcType=VARCHAR}


  and REMARK = #{remark,jdbcType=VARCHAR}
